import React from 'react';
import { motion } from 'framer-motion';
import './Button.css';

function Button({ text, onClick }) {
  const variants = {
    initial: { backgroundColor: '#000', color: '#fff' },
    hover: { backgroundColor: '#fff', color: '#000' }
  };

  return (
    <motion.button
      className="button"
      variants={variants}
      initial="initial"
      whileHover="hover"
      onClick={onClick}
    >
      {text}
    </motion.button>
  );
}

export default Button;
