import React, { useRef, useEffect } from "react";
import backgroundImage from '../assets/immagine-hero.jpg';
import './Home.css';
import Heroone from "./Heroone";
import Herotwo from "./Herotwo";
import Herothree from "./Herothree";

const homeContainerStyle = {
  backgroundImage: `url(${backgroundImage})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  backgroundRepeat: 'no-repeat',
  width: '100%',
  height: '100vh',
};

function Home() {
  const homeRef = useRef(null);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div ref={homeRef}>
      <div style={homeContainerStyle} alt="Background image description"></div>
      <div className="banner" >
        <p>Outerweare</p>
        <h1>KEEP ON MOVING</h1>
        <p>Vieni a scoprire la nostra nuova collezione primavera estate 2023</p>
        <button className="slide-in-button">
          <a href="ww" alt="" rel=""><span>Vai alla collezione</span></a>
        </button>
        <a href="mmm">Hover this link</a>
      </div>
      <Heroone />
      <Herotwo />
      <Herothree />
    </div>
  );
}

export default Home;
