import React, { useRef, useEffect } from "react";
import backgroundImage from '../assets/immagine-hero.jpg';
import './Home.css';


const homeContainerStyle = {
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    width: '100%',
    height: '100vh',
  };

  function Home() {
    const sectionRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const offset = window.pageYOffset;
      if (sectionRef.current) {
        sectionRef.current.style.backgroundPositionY = `${offset * 0.5}px`;
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

    return (
      <div>
        <div style={homeContainerStyle} alt="Background image description">
        
        </div>
        <div className="home-container"></div>
      <div className="parallax" ref={sectionRef}>
        <h1>Hello World</h1>
        <h1>Hello World</h1>
        <h1>Hello World</h1>
      </div>
      </div>
      
    );
  }
  
  export default Home;
