import React, { useRef, useEffect } from "react";
import backgroundImage from '../assets/immagine-hero.jpg';
import './Home.css';
import Heroone from "./Heroone";
import Herotwo from "./Herotwo";
import Herothree from "./Herothree";
import { Link } from 'react-router-dom';
import Carousel from "./Carousel";

const homeContainerStyle = {
  backgroundImage: `url(${backgroundImage})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  backgroundRepeat: 'no-repeat',
  width: '100%',
  height: '100vh',
};

function Home() {
  const homeRef = useRef(null);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div ref={homeRef}>
      <div style={homeContainerStyle} alt="Background image description"></div>
      <div className="banner" >
        <p>Outerweare</p>
        <h1>KEEP ON MOVING</h1>
        <p>Vieni a scoprire la nostra nuova collezione primavera estate 2023</p>
        <Link to="/collection" className="button">VAI ALLA COLLEZIONE</Link>
      </div>
      <Heroone />
      <Herotwo />
      <Herothree />
      <h1>Ultimi arrivi collezione primavera estate 2023</h1>
      <Carousel />
    </div>
  );
}

export default Home;
