import React, { useEffect, useRef } from 'react';
import './Contact.css';
import Footer from './Footer';
import {motion} from 'framer-motion';
import emailjs from '@emailjs/browser';

function Contact() {
    useEffect(() => {
        window.scrollTo(0, 0);
      }, [])

      const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  
    
    return <div className="container">
        <h1>Contact</h1>
       <motion.div 
        initial={{x: 100}}
        animate={{x: 0}}
        transition={{duration: 0.8}}>
       <div className="form-container">
        <div className="formbold-main-wrapper">
  <div className="formbold-form-wrapper">
    <form ref={form} onSubmit={sendEmail}>
      <div className="formbold-mb-5">
        <label for="name" className="formbold-form-label"> Full Name </label>
        <input
          type="text"
          name="name"
          id="name"
          placeholder="Full Name"
          className="formbold-form-input"
        />
      </div>

      <div className="formbold-mb-5">
        <label for="email" className="formbold-form-label"> Email Address </label>
        <input
          type="email"
          name="email"
          id="email"
          placeholder="Enter your email"
          className="formbold-form-input"
        />
      </div>

      <div className="formbold-mb-5">
        <label for="subject" className="formbold-form-label"> Subject </label>
        <input
          type="text"
          name="subject"
          id="subject"
          placeholder="Enter your subject"
          className="formbold-form-input"
        />
      </div>

      <div className="formbold-mb-5">
        <label for="message" className="formbold-form-label"> Message </label>
        <textarea
          rows="6"
          name="message"
          id="message"
          placeholder="Type your message"
          className="formbold-form-input"
        ></textarea>
      </div>

      <div>
        <button type='submit' className="formbold-btn">Submit</button>
      </div>
    </form>
  </div>
</div>
        </div>
        </motion.div>
        <Footer></Footer>
    </div>
    
}

export default Contact;
